import Cell from "./Cell";

function Table({x, y}) {

  const columns = [];
  const heads = [];

  for (let i = 0; i < x; i++) {
    const rows = [];
    for(let j = 0; j < y; j++){
      if (i===0){
        const letters = ' abcdefghijklmnopqrstuvwxyz'.split('')
        heads.push(

          <th className="bg-slate-300 border-slate-300 " size={"10"} role="presentation">
            {letters[j]}
          </th>
        )
      }else{
        rows.push(<Cell key={`${i}-${j}`} />);
      }
    } 
    columns.push([rows]) 
  }

  const rendered = columns.map((column) => {
    return <tr>{column}</tr>;
  }); 


  return (
    <div className="table">
      <table className="table" cellPadding="0" cellSpacing="0" >
        <thead>
          <tr>
            {heads}
          </tr>
        </thead>

        <tbody>
          {rendered}
        </tbody>

      </table>
    </div>

  );

}

export default Table;
