import { useState } from "react";

function Cell({ x, y, cells, setCells }) {
  let cell = cells[x][y];
  const info = cell.info;
  const [isFocused, setIsFocused] = useState(false);

  const handleChange = (event) => {
    cells[x][y] = { ...cell, info: { ...info, method: event.target.value } };
    setCells([...cells])
  }

  const handleBlur = () => {
    let sum = info.method;
    if (info.method.startsWith('=') && info.method.length > 1) {
      sum = doSum(info.method, 0);
    }
    cells[x][y] = { ...cell, info: { value: sum, method: info.method } };
    setCells([...cells])
    setIsFocused(false);
  }

  const doSum = (val, sum) => {
    let indexSum = val.indexOf('+');
    let indexSub = val.indexOf('-');

    if (indexSum > indexSub) {
      let indexAux = val.slice(indexSum + 1, val.length).indexOf('+')
      if (indexAux > 0) {
        sum += checkCell(val.slice(1, indexSum));
        return doSum(val.slice(indexSum + 1, val.length), sum);
      } else {
        sum += checkCell(val.slice(1, indexSum)) + checkCell(val.slice(indexSum + 1, val.length))
        return sum
        //cell.value = sum
      }
    }// else if (indexSub > indexSum) {
    //cell.value = (checkCell(val.slice(1, indexSub)) - checkCell(val.slice(indexSub + 1, val.length)));
    //}

  }
  // convertir letra a num
  //fijars en cells coord y sacar value
  const checkCell = (coord) => {
    const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    let toParse = coord;
    let letter = coord.slice(0, 1);

    //console.log(coordX)
    let toReturn;

    try {
      toReturn = parseInt(toParse);
    } catch (error) {
      l
      let coordX = letters.indexOf(letter);
      console.log(coordX);
      toReturn = parseInt(cells[coordX][parseInt(coord.slice(2, 3))]);
    }
    //console.log(toReturn);
    return toReturn;
  }

  const handleFocus = () => {
    setIsFocused(true);
  }

  return (
    <td className="tdinput border-2 border-slate-300">
      <input id={`${x}${y}`} size={"10"} type="input" value={isFocused ? info.method : info.value} onChange={handleChange} onBlur={handleBlur} onFocus={handleFocus}></input>
    </td>
  );
}

export default Cell;
