import { useEffect, useState } from "react";
import Cell from "./Cell";

function Table({ x, y }) {
  const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('')

  const heads = [];

  const [cells, setCells] = useState([])

  useEffect(() => {
    initCells();
  }, [x, y])

  const initCells = () => {
    const columns = [];
    for (let i = 0; i <= x; i++) {
      const rows = [];
      // for(let j = 0; j <= y; j++){
      //   if (i===0){
      //     heads.push(<th className="bg-slate-200 border-2 border-slate-300">{letters[j] }</th>);
      //   }else if(j===0){
      //     rows.push(<th className="bg-slate-200 border-2 border-slate-300">{i}</th>);
      //   }
      //   else{
      //     rows.push(<Cell x={letters[j]} y={i.toString()} cell={{value:'', method:''}}/>);
      //   }
      // } 
      for (let j = 0; j <= y; j++) {
        rows.push({ value: '', method: '' });
      }
      columns.push([rows]);
    }
    setCells(columns)
  }

  const renderColumns = cells.map((column) => {
    return <tr>{column}</tr>;
  });


  return (
    <table>
      <thead>
        <tr>{heads}</tr>
      </thead>
      <tbody>
        {cells.map((column) => {
          return <tr>{column}</tr>;
        })}
      </tbody>
    </table>
  );
}

export default Table;
