import Cell from "./Cell";

function Table({x, y}) {

  const columns = [];
  const heads = [];

  for (let i = 0; i < x; i++) {
    const rows = [];
    for(let j = 0; j < y; j++){
      if (i===0){
        const alpha = ' abcdefghijklmnopqrstuvwxyz'.split('')
        heads.push(
          <th className="border border-slate-300 " size={"10"} role="presentation">
            {alpha[j]}
          </th>
        )
      }else{
        rows.push(<Cell key={`${i}-${j}`} />);
      }
    } 
    columns.push(rows) 
  }


  return (
    <table className="table-auto" cellPadding="0" cellSpacing="0" border="2">
      <thead>
        <tr>
          {heads}
        </tr>
      </thead>

      <tbody>
        <tr>{columns}</tr>
      </tbody>

    </table>
  );

}

export default Table;
