import Cell from "./Cell";

function Table({x, y}) {
  const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('')
  const columns = [];
  const heads = [];

  for (let i = 0; i < x; i++) {
    const rows = [];
    for(let j = 0; j < y; j++){
      if (i===0){

        heads.push(

          <th className="bg-slate-200 border-2 border-slate-300" role="presentation">
            {letters[j]}
          </th>
        )
      }else if(j===0){
        rows.push(<th className="bg-slate-200 border-2 border-slate-300" role="presentation">{i}</th>)
      }
      else{
        rows.push(<Cell key={`${i}-${j}`} />);
      }
    } 
    columns.push([rows]) 
  }

  const rendered = columns.map((column) => {
    return <tr>{column}</tr>;
  }); 


  return (
    <table className="table" cellPadding="0" cellSpacing="0" >
      <thead>
        <tr>
          {heads}
        </tr>
      </thead>

      <tbody>
        {rendered}
      </tbody>

    </table>
  );

}

export default Table;
