import { useEffect, useState } from "react";
import Cell from "./Cell";

function Table({ x, y }) {
  const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('')

  const heads = [];

  const [cells, setCells] = useState([])

  useEffect(() => {
    initCells();
  }, [x, y])

  const initCells = () => {
    const columns = [];
    for (let i = 0; i <= x; i++) {
      const rows = [];
      for (let j = 0; j <= y; j++) {
        rows.push({ x: j, y: i, info: { value: '', method: '' } });
      }
      columns.push([rows]);
    }
    setCells(columns)
  }

  const renderColumns = cells.map((column) => {
    return <tr>{column}</tr>;
  });


  return (
    <table>
      <thead>
        <tr>{heads}</tr>
      </thead>
      <tbody>
        {cells.map(column =>
          column.map(({ x, y, info }) => {
            if (y === 0) {
              return <th className="bg-slate-200 border-2 border-slate-300">{letters[x]}</th>;
            } else if (x === 0) {
              return <th className="bg-slate-200 border-2 border-slate-300">{y}</th>;
            } else {
              return <Cell x={x} y={y} info={info} setCells={setCells} />
            }
          }))
        }
      </tbody>
    </table>
  );
}

export default Table;
