import { useState } from "react";

function Cell({x,y}) {
  const [value, setValue] = useState('');

  const handleChange = (event) => {
    setValue(event.target.value);
  }

  const handleBlur = () => {
    if (value.startsWith('=')){
      doSum(value,0);
    }
  }

  const doSum = (val, sum) => {
    let indexSum = val.indexOf('+');
    let indexSub = val.indexOf('-');

    if(indexSum > indexSub){
      let indexAux = val.slice(indexSum+1,val.length).indexOf('+')
      if(indexAux > 0 ){
        sum += checkCell(val.slice(1,indexSum)) ;
        doSum(val.slice(indexSum + 1,val.length), sum);
      }else{
        sum += checkCell(val.slice(0,indexSum)) + checkCell(val.slice(indexSum+1,val.length))
        setValue(sum)
      }      
    }else if(indexSub>indexSum){
      setValue(checkCell(val.slice(1,indexSub)) - checkCell(val.slice(indexSub+1,val.length)));
    }

  }

  const checkCell = (num) => {
    let toReturn;
    try {
      toReturn = parseInt(document.getElementById(num).getAttribute('value'));
    } catch (error) {
      toReturn = parseInt(num);
    }
    return toReturn;
  }

  return (
    <td className="tdinput border-2 border-slate-300">
      <input id={`${x}${y}`} size={"10"} type="input" value={value} onChange={handleChange} onBlur={handleBlur}></input>
    </td>
  );
}

export default Cell;
