function Table({x, y}) {
  const rows = [];

  for (let i = 0; i < x; i++) {
    for(let j = 0; j < y; j++){
      if (i===0){
        const alpha = ' abcdefghijklmnopqrstuvwxyz'.split('')
        rows.push(
          <span
            role="presentation">
            {alpha[this.props.x]}
          </span>
        )
      }
      rows.push(<Cell key={`${i}-${j}`} />);
    }


  }
  return <>{rows}</>;

}

export default Table;
