import { useState } from "react";

function Cell({x,y}) {
  const [value, setValue] = useState('')

  const handleChange = (event) => {
    setValue(event.target.value)
  }

  const handleBlur = () => {
    if (value.startsWith('=')){
      doSum(value, 0)
    }
  }

  const doSum = (val, sum) => {
    let index = val.indexOf("+")
    let sum_now = 0;
    console.log(index, sum_now)
    if (index > 0){
      sum_now = checkCell(val.slice(1,index)) + sum;
      doSum(val.slice(index+1,val.length), sum_now)
    }

    setValue(parseInt(sum_now) + parseInt(val.slice(index+1,val.length)))
  }

  const checkCell = (num) => {
    let toReturn;
    try {
      toReturn = document.getElementById(num).getAttribute('value');
    } catch (error) {
      toReturn = parseInt(num);
    }

    return toReturn;
  }

  return (
    <td className="tdinput border-2 border-slate-300">
      <input id={`${x}${y}`} size={"10"} type="input" value={value} onChange={handleChange} onBlur={handleBlur}></input>
    </td>
  );
}

export default Cell;
