import { useState } from "react";

function Cell({ x, y, cells, setCells }) {
  let cell = cells[x][y];
  const info = cell.info;
  const [isFocused, setIsFocused] = useState(false);

  const handleChange = (event) => {
    cells[x][y] = { ...cell, info: { ...info, method: event.target.value } };
    setCells([...cells])
  }

  const handleBlur = () => {
    let sum = info.method;
    if (info.method.startsWith('=') && info.method.length > 1) {
      sum = doSum(info.method);
    }
    cells[x][y] = { ...cell, info: { value: sum, method: info.method } };
    setCells([...cells])
    setIsFocused(false);
  }

  const doSum = (val, desde) => {
    //const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    val = val.replace('=', '')
    console.log(val)
    //let index = val.indexOf(letters);
    //console.log(index)

    let indexSum = val.indexOf('+');
    let indexSub = val.indexOf('-');
    let firstIndex;
    let op;
    if (0 < indexSum < indexSub || indexSub < 0 < indexSum) {//(indexSum < indexSub && indexSum >= 0) || (indexSum > indexSub && indexSum >= 0)) {
      firstIndex = indexSum;
      op = '+';
    } else if (0 < indexSub < indexSum || indexSum < 0 < indexSub) {
      firstIndex = indexSub;
      op = '-';
    } else {
      return eval(checkCell(val));
    }
    val = checkCell(val.slice(desde, firstIndex)) + `${op}` + doSum(val.slice(firstIndex + 1, val.length), firstIndex + 1);
    console.log(val)
    return eval(val)

    // if (indexSum > indexSub) {
    //   let indexAux = val.slice(indexSum + 1, val.length).indexOf('+')
    //   if (indexAux > 0) {
    //     sum += checkCell(val.slice(1, indexSum));
    //     return doSum(val.slice(indexSum + 1, val.length), sum);
    //   } else {
    //     sum += checkCell(val.slice(1, indexSum)) + checkCell(val.slice(indexSum + 1, val.length))
    //     return sum
    //     //cell.value = sum
    //   }
    // } else if (indexSub > indexSum) {
    //   cell.value = (checkCell(val.slice(1, indexSub)) - checkCell(val.slice(indexSub + 1, val.length)));
    // }

  }

  const checkCell = (coord) => {
    console.log(coord)
    const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');
    let index = letters.indexOf(coord.slice(0, 1));
    let toReturn;
    if (index > 0) {
      toReturn = cells[index][parseInt(coord.slice(1, 2))].info.value;
    } else {
      toReturn = coord;
    }
    return toReturn;
  }

  const handleFocus = () => {
    setIsFocused(true);
  }

  return (
    <td className="tdinput border-2 border-slate-300">
      <input id={`${x}${y}`} size={"10"} type="input" value={isFocused ? info.method : info.value} onChange={handleChange} onBlur={handleBlur} onFocus={handleFocus}></input>
    </td>
  );
}

export default Cell;
