import { useState } from "react";

function Cell() {
  const [value, setValue] = useState('')

  const handleChange = (event) => {
    console.log(event.target.value)
    setValue(event.target.value)
  }

  const handleBlur = () => {
    if (value.startsWith('=')){
      doSum(value)
    }
  }

  const doSum = (val) => {
    let index = val.indexOf('+')
    setValue(parseInt(val.slice(1,index)) + parseInt(val.slice(index+1,val.length)))
  }

  return <input size={{width:'30px'}} type="input" value={value} onChange={handleChange} onBlur={handleBlur}></input>
}

export default Cell;
