import { useState } from "react";

function Cell() {
  const [value, setValue] = useState('')

  const handleChange = (event) => {
    console.log(event.target.value)
    setValue(event.target.value)
  }

  const handleBlur = () => {
    if (value.toString().slice(0,1) === '='){
      value.slice(1)
      console.log('equals')
      console.log(value)
    }
    console.log("blured")
  }

  return <input type="input" value={value} onChange={handleChange} onBlur={handleBlur}></input>
}

export default Cell;
