import { useState } from "react";

function Cell({x,y,cell}) {
  //const [value, setValue] = useState('');

  const handleChange = (event) => {
    //setValue(event.target.value);
    console.log(event.target.value);
    cell.value = event.target.value;
  }

  const handleBlur = () => {
    if (cell.value.startsWith('=') && cell.value.length > 1){
      doSum(cell.value,0);
    }else{
      cell.value = '';
    }
  }

  const doSum = (val, sum) => {
    let indexSum = val.indexOf('+');
    let indexSub = val.indexOf('-');

    if(indexSum > indexSub){
      let indexAux = val.slice(indexSum+1,val.length).indexOf('+')
      if(indexAux > 0 ){
        sum += checkCell(val.slice(1,indexSum)) ;
        doSum(val.slice(indexSum + 1,val.length), sum);
      }else{
        sum += checkCell(val.slice(1,indexSum)) + checkCell(val.slice(indexSum+1,val.length))
        cell.value = sum;
      }      
    }else if(indexSub > indexSum){
      cell.value = (checkCell(val.slice(1,indexSub)) - checkCell(val.slice(indexSub+1,val.length)));
    }

  }

  const checkCell = (num) => {
    let toReturn;
    try {
      toReturn = parseInt(document.getElementById(num).getAttribute('value'));
    } catch (error) {
      toReturn = parseInt(num);
    }
    return toReturn;
  }

  return (
    <td className="tdinput border-2 border-slate-300">
      <input id={`${x}${y}`} size={"10"} type="input" value={cell.value} onChange={handleChange} onBlur={handleBlur}></input>
    </td>
  );
}

export default Cell;
