import Cell from "./Cell";

function Table({x, y}) {
  const letters = ' ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('')
  const columns = [];
  const heads = [];

  for (let i = 0; i <= x; i++) {
    const rows = [];
    for(let j = 0; j <= y; j++){
      if (i===0){

        heads.push(
          <th className="bg-slate-200 border-2 border-slate-300">
            {letters[j]}
          </th>
        )
      }else if(j===0){
        rows.push(<th className="bg-slate-200 border-2 border-slate-300">{i}</th>)
      }
      else{
        rows.push(<Cell x={letters[i]} y={j}/>);
      }
    } 
    columns.push([rows]) 
  }

  const rendered = columns.map((column) => {
    return <tr>{column}</tr>;
  }); 


  return (
    <table>
      <thead>
        <tr>
          {heads}
        </tr>
      </thead>

      <tbody>
        {rendered}
      </tbody>

    </table>
  );

}

export default Table;
