  const handleBlur = () => {
    cell.method = value.toString();
    console.log(cell.method, cell.value)
    if (cell.method.toString().startsWith('=') && cell.method.toString().length > 1) {
      doSum(value, 0);
    } else {
      cell.value = value
      setValue(parseInt(cell.value))
    }
  }

  const doSum = (val, sum) => {
    let indexSum = val.indexOf('+');
    let indexSub = val.indexOf('-');

    if (indexSum > indexSub) {
      let indexAux = val.slice(indexSum + 1, val.length).indexOf('+')
      if (indexAux > 0) {
        sum += checkCell(val.slice(1, indexSum));
        doSum(val.slice(indexSum + 1, val.length), sum);
      } else {
        sum += checkCell(val.slice(1, indexSum)) + checkCell(val.slice(indexSum + 1, val.length))
        setValue(sum);
        cell.value = sum
      }
    } else if (indexSub > indexSum) {
      cell.value = (checkCell(val.slice(1, indexSub)) - checkCell(val.slice(indexSub + 1, val.length)));
    }

  }

  const checkCell = (num) => {
    let toReturn;
    try {
      toReturn = parseInt(document.getElementById(num).getAttribute('value'));
    } catch (error) {
      toReturn = parseInt(num);
    }
    return toReturn;
  }

  const handleDoubleClick = () => {
    setValue(cell.method);
    doSum(cell.method, 0)
  }

  return (
    <td className="tdinput border-2 border-slate-300">
      <input id={`${x}${y}`} size={"10"} type="input" value={value} onChange={handleChange} onBlur={handleBlur} onDoubleClick={handleDoubleClick}></input>
    </td>
  );
}

export default Cell;
