import Cell from "./Cell";

function Table({x, y}) {
  const rows = [];
  const heads = [];

  for (let i = 0; i < x; i++) {
    for(let j = 0; j < y; j++){
      if (i===0){
        const alpha = ' abcdefghijklmnopqrstuvwxyz'.split('')
        heads.push(
          <th
            class="border border-slate-300 "
            role="presentation">
            {alpha[j]}
          </th>

        )
        console.log(alpha[j])
      }else{
        rows.push(<Cell key={`${i}-${j}`} />);
      }

    }


  }
  return (
    <table class="table-auto" >
      <thead>
        <tr>
          {heads}
        </tr>
      </thead>

      <tbody>
        <tr>{rows}</tr>
      </tbody>

    </table>
  );

}

export default Table;
