    setValue(event.target.value)
  }

  const handleBlur = () => {
    if (value.startsWith('=')){
      //setValue(value.toString().replace('=',''))
      console.log('equals')

    }
    console.log(value)
    console.log("blured")
    doSum(value)
  }

  const doSum = (val) => {
    console.log(val.toString().indexOf('+'))
  }

  return <input type="input" value={value} onChange={handleChange} onBlur={handleBlur}></input>
}

