    setCells([...cells])
  }

  const handleBlur = () => {
    let sum = value;
    //cell.method = value.toString();
    //console.log(cell.method, cell.value)
    if (value.toString().startsWith('=') && value.toString().length > 1) {
      sum = doSum(value, 0);
    } else {
      //cell.value = value
      cells[x][y] = { ...cells[x][y], info: { value: sum, method: value.toString() } };
      setCells([...cells])
    }
  }

  const doSum = (val, sum) => {
    let indexSum = val.indexOf('+');
    let indexSub = val.indexOf('-');

    if (indexSum > indexSub) {
      let indexAux = val.slice(indexSum + 1, val.length).indexOf('+')
      if (indexAux > 0) {
        sum += checkCell(val.slice(1, indexSum));
        return doSum(val.slice(indexSum + 1, val.length), sum);
      } else {
        sum += checkCell(val.slice(1, indexSum)) + checkCell(val.slice(indexSum + 1, val.length))
        return sum
        //cell.value = sum
      }
    }// else if (indexSub > indexSum) {
